mapper.update:
update boardContent
set file=#{file}
where id = #{id}

update boardContent
set content=#{content}
where id = #{id}

update boardContent
set rollBack=1
where id = #{id}

update boardContent
set liked=liked + 1
where id = #{id}

update boardContent
set liked=liked - 1
where id = #{id}

mapper.delete:
delete
from boardContent
where contentSerialNumber = #{contentSerialNumber}
  and id = #{id}
